        <div class="flex-1 ml-4 flex flex-col">
            <div class="font-medium" v-if="accessToken.institution">{{ accessToken.institution.name }}</div>
            <div class=" text-sm italic text-gray-600">{{ accessToken.accounts.map(account => account.name).join(', ') }}</div>
        </div>
    </div>
</template>

<script>
    export default {
        props: ['accessToken'],
        data() {
            return {
                loading: false,
            }
        },
        methods: {
            async pullAccounts(access_token_id) {
                this.loading = true;
                await axios.post('/api/actions/refresh-accounts-for', {
                    access_token_id,
                });
                Bus.$emit('fetchAccounts')

                this.loading = false;
            }
        }
    }
</script>
